#Get the 6 digit TOTP code of a specific entry in the database if there is one using keepassxc-cli export PATH='/bin:/usr/local/bin/:/usr/bin:/Applications/KeePassXC.app/Contents/MacOS/:/opt/homebrew/bin:${PATH}' function get_TOTP { if [ ! -z "${keePassKeyFile}" ]; then security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" |\ keepassxc-cli show --key-file "${keePassKeyFile}" -q -t "${database}" "$1" else security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" |\ keepassxc-cli show -q -t "${database}" "$1" fi }
function get_errorInfo { exec 3<&1 if [ ! -z "${keePassKeyFile}" ]; then security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" 2>&3 |\ keepassxc-cli ls -R --key-file "${keePassKeyFile}" -f "$database" 2>&3 | grep -Ev '(/|\[empty\]?)$' else security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" 2>&3 |\ keepassxc-cli ls -R -f "$database" 2>&3 | grep -Ev '(/|\[empty\]?)$' fi exec 3>&- }
data=$(get_TOTP "$1") if [ $? -ne 0 ]; then echo $(get_errorInfo) else echo -n $data fi

export PATH='/bin:/usr/local/bin/:/usr/bin:/Applications/KeePassXC.app/Contents/MacOS/:/opt/homebrew/bin:${PATH}'
function get_db_keys { if [ ! -z "${keePassKeyFile}" ]; then security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" |\ keepassxc-cli search --key-file "${keePassKeyFile}" "${database}" - -q else security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" |\ keepassxc-cli search "${database}" - -q fi }
function get_keys { if [ -z "${cacheFile}" ]; then get_db_keys else #¬†Cache is enabled if [ -f "${cacheFile}" ]; then lastModifiedTime=$(GetFileInfo -d "${cacheFile}") else lastModifiedTime=$(date +"%m/%d/%Y %H:%M:%S") fi lastModifiedTime=$(date -jf "%m/%d/%Y %H:%M:%S" "${lastModifiedTime}" +%s) currTime=$(date +%s) interval=$( expr $currTime - $lastModifiedTime ) if [ ! -f "${cacheFile}" ] || [ "${interval}" -gt "${cacheTimeout}" ]; then # Update cache echo "$(get_db_keys)" > "${cacheFile}" fi #¬†Get keys from the cache cat "${cacheFile}" | grep -i "${query}" fi }
function get_errorInfo { exec 3<&1 if [ ! -z "${keePassKeyFile}" ]; then security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" 2>&3 |\ keepassxc-cli search --key-file "${keePassKeyFile}" "${database}" - -q 2>&3 else security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" 2>&3 |\ keepassxc-cli search "${database}" - -q 2>&3 fi exec 3>&- }
if [[ -z "${database}" ]] || [[ -z "${keychain}" ]]; then echo "{\"items\": [{\"title\":\"Not configured, please run: kpassinit\"}]}"; exit fi
keys=($(get_keys)) if [ $? -ne 0 ]; then info=$(get_errorInfo | sed 's/"/\\"/g') info=${info//$'\n'/} echo "{\"items\": [{\"title\":\"Error listing database, please check config: Error: ${info}\"}]}"; exit else echo ${keys[@]} | sed 's/ \//\n\//g' | awk -v iconPath="${PWD}/icon.png" '{printf "{\"uid\":\"%s\", \"title\":\"%s\", \"subtitle\":\"%s\", \"arg\":\"%s\", \"autocomplete\": \"%s\", \"icon\":{\"type\":\"png\", \"path\": \"%s\"}}", $0, substr($0,2, length($0)), $0, $0, $0, iconPath}' | jq -c -s '{"items": .}' fi

export PATH='/bin:/usr/local/bin/:/usr/bin:/Applications/KeePassXC.app/Contents/MacOS/:/opt/homebrew/bin:${PATH}'
function get_keys { if [ ! -z "${keePassKeyFile}" ]; then security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" |\ keepassxc-cli show --key-file "${keePassKeyFile}" -q -a Password "${database}" "$1" else security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" |\ keepassxc-cli show -q -a Password "${database}" "$1" fi }
function get_errorInfo { exec 3<&1 if [ ! -z "${keePassKeyFile}" ]; then security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" 2>&3 |\ keepassxc-cli ls -R --key-file "${keePassKeyFile}" -f "$database" 2>&3 | grep -Ev '(/|\[empty\]?)$' else security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" 2>&3 |\ keepassxc-cli ls -R -f "$database" 2>&3 | grep -Ev '(/|\[empty\]?)$' fi exec 3>&- }
data=$(get_keys "$1") if [ $? -ne 0 ]; then echo $(get_errorInfo) else echo -n $data fi

