#Get the URL of a specific entry in the database export PATH='/bin:/usr/local/bin/:/usr/bin:/Applications/KeePassXC.app/Contents/MacOS/:/opt/homebrew/bin:${PATH}' function get_url { if [ ! -z "${keePassKeyFile}" ]; then security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" |\ keepassxc-cli show --key-file "${keePassKeyFile}" -q -a URL "${database}" "$1" else security find-generic-password -a $(id -un) -c 'kpas' -C 'kpas' -s "${keychainItem}" -w "${keychain}" |\ keepassxc-cli show -q -a URL "${database}" "$1" fi }
